/* global goog, lime, obacht */
/* jshint devel:true */

goog.provide('obacht.Bonus');

// Obacht Requirements
goog.require('obacht.options');

// LimeJS Requirements
goog.require('lime.RoundedRect');

// Closure Library Requirements
goog.require('goog.pubsub.PubSub');

/**
 * Boni Object. Bonis are generated by the Server
 *
 * @constructor
 */
obacht.Bonus = function(type) {
	"use strict";
	
	////////////////
    // ATTRIBUTES //
    ////////////////
	this.type = type;
	this.fill = obacht.options.bonus.general.path + this.type + '.png';
	
	////////////////////
    // LIMEJS OBJECTS //
    ////////////////////
	this.bonusButton = new lime.RoundedRect().setSize(obacht.options.bonus.general.size, obacht.options.bonus.general.size).setPosition(obacht.options.bonus.general.x, obacht.options.bonus.general.y).setFill('#666666').setOpacity(0.5).setAnchorPoint(0, 0).setRadius(15);
    this.layer = new lime.Layer().setSize(obacht.options.graphics.VIEWPORT_WIDTH, obacht.options.graphics.VIEWPORT_HEIGHT);
    this.layer.appendChild(this.bonusButton);
    
    /////////////////////////
    /* SUBSCRIBE TO EVENTS */
    /////////////////////////
   /* obacht.playerController.events.subscribe('getBoni', function(inventary) {
		console.log('wow ' + inventary);
    });*/

    
    
};

obacht.Bonus.prototype = {
    
    moveto: function(inventary) {
        "use strict";
        console.log('move');
        //legt Bonus im Iventar ab        
    }

};
